CREATE TABLE book(
  title VARCHAR(128),
  id INTEGER,
  authors TEXT,
  categories TEXT,
  url VARCHAR(128),
  preface TEXT,
  PRIMARY KEY(id)
);

CREATE TABLE author(
    role VARCHAR(32),
    firstname VARCHAR(32),
    lastname VARCHAR(32),
    birthyear INTEGER,
    deathyear INTEGER,
    imgurl VARCHAR(128),
    about TEXT,
    signature VARCHAR(128)
);

CREATE TABLE category(
    category VARCHAR(64)
);






